import React, { Component } from 'react';
// import './Movie.css';

const axios = require('axios');

export class Movie_helper extends Component {
  constructor()
  {
    super();
    this.state = { src: '', title: '', imdb: '', director: '', plot: ''}
  }


  componentDidUpdate(prevProps, prevState, snapshot){
    if(this.props.movie !== prevProps.movie) {
      this.render();
    }
    if(this.state.shouldUpdate !== prevState.shouldUpdate){
      this.render();
    }
  }


  render() {
    return(
      <div className='frame'>
        <img src={this.props.src}
          onClick={this.props.enlarge.bind(this, this.props.src, this.props.title, this.props.director, this.props.imdb, this.props.movie, this.props.plot)} alt={this.state.title}/>
      </div>      
    );
  }
}
export default Movie_helper;
